import { tool } from "ai"
import { z } from "zod"
import { extractFigmaStructure, listComponents, listFrames } from "./lib/parser"
import type { ExtractedData, FigmaFile } from "./lib/types"

const FIGMA_API_BASE = "https://api.figma.com/v1"

let cachedFigmaData: ExtractedData | null = null

export function getCachedFigmaData(): ExtractedData | null {
	return cachedFigmaData
}

export function setCachedFigmaData(data: ExtractedData | null): void {
	cachedFigmaData = data
}

function parseFileKeyFromUrl(urlOrKey: string): string {
	if (!urlOrKey.includes("/")) {
		return urlOrKey
	}

	const patterns = [
		/figma\.com\/file\/([a-zA-Z0-9]+)/,
		/figma\.com\/design\/([a-zA-Z0-9]+)/,
	]

	for (const pattern of patterns) {
		const match = urlOrKey.match(pattern)
		if (match?.[1]) {
			return match[1]
		}
	}

	return urlOrKey
}

async function fetchFigmaFile(
	fileKey: string,
	token: string,
): Promise<FigmaFile> {
	const response = await fetch(`${FIGMA_API_BASE}/files/${fileKey}`, {
		headers: {
			"X-Figma-Token": token,
		},
	})

	if (!response.ok) {
		const errorText = await response.text()
		throw new Error(
			`Figma API error (${response.status}): ${errorText || response.statusText}`,
		)
	}

	return response.json() as Promise<FigmaFile>
}

const description = `Fetch a Figma file and extract its structure for migration.

This tool:
1. Fetches the Figma file from the REST API using FIGMA_TOKEN
2. Extracts all pages, sections, frames, and components
3. Caches the data for subsequent tool calls
4. Returns a summary of what was found

Input can be either:
- A Figma file key (e.g., "abc123XYZ")
- A full Figma URL (e.g., "https://www.figma.com/file/abc123XYZ/My-Design")

After fetching, use the migration tools to process components and pages.`

export function createFigmaFetchTool(figmaToken?: string) {
	return tool({
		description,
		inputSchema: z.object({
			fileKeyOrUrl: z
				.string()
				.describe("The Figma file key or full URL to fetch"),
			skipInvisible: z
				.boolean()
				.default(true)
				.describe("Skip invisible nodes in the design"),
		}),
		outputSchema: z.union([
			z.object({
				status: z.literal("pending"),
				message: z.string(),
			}),
			z.object({
				status: z.literal("success"),
				message: z.string(),
				fileKey: z.string(),
				summary: z.object({
					totalPages: z.number(),
					totalSections: z.number(),
					totalFrames: z.number(),
					totalComponents: z.number(),
					componentsWithDefinition: z.number(),
					topComponents: z.array(
						z.object({
							name: z.string(),
							instanceCount: z.number(),
						}),
					),
				}),
			}),
			z.object({
				status: z.literal("error"),
				message: z.string(),
				error: z.string(),
			}),
		]),
		toModelOutput: (output) => {
			if (output.status === "error") {
				return {
					type: "error-text",
					value: `Figma fetch failed: ${output.error}`,
				}
			}
			if (output.status === "success") {
				const { summary } = output
				const topList = summary.topComponents
					.map((c) => `  - ${c.name} (${c.instanceCount}x)`)
					.join("\n")
				return {
					type: "text",
					value: `Fetched Figma file: ${output.fileKey}

Summary:
- Pages: ${summary.totalPages}
- Sections: ${summary.totalSections}
- Frames: ${summary.totalFrames}
- Components: ${summary.totalComponents} (${summary.componentsWithDefinition} with definitions)

Top components by usage:
${topList}

Use migrationInit to start the migration process.`,
				}
			}
			return { type: "text", value: output.message }
		},
		async *execute({ fileKeyOrUrl, skipInvisible }) {
			const token = figmaToken || process.env.FIGMA_TOKEN

			if (!token) {
				yield {
					status: "error",
					message: "No Figma token provided",
					error:
						"FIGMA_TOKEN environment variable is not set. Please set it to your Figma personal access token.",
				}
				return
			}

			const fileKey = parseFileKeyFromUrl(fileKeyOrUrl)

			yield {
				status: "pending",
				message: `Fetching Figma file ${fileKey}...`,
			}

			try {
				const fileData = await fetchFigmaFile(fileKey, token)
				const extracted = extractFigmaStructure(fileData, { skipInvisible })

				cachedFigmaData = extracted

				const components = listComponents(extracted)
				const frames = listFrames(extracted)
				const componentsWithDef = components.filter(
					(c) => extracted.components[c.id]?.definition !== null,
				).length

				yield {
					status: "success",
					message: `Successfully fetched and parsed Figma file`,
					fileKey,
					summary: {
						totalPages: extracted.pages.length,
						totalSections: Object.keys(extracted.sections).length,
						totalFrames: frames.length,
						totalComponents: components.length,
						componentsWithDefinition: componentsWithDef,
						topComponents: components.slice(0, 10).map((c) => ({
							name: c.name,
							instanceCount: c.instanceCount,
						})),
					},
				}
			} catch (error) {
				yield {
					status: "error",
					message: `Failed to fetch Figma file`,
					error: error instanceof Error ? error.message : String(error),
				}
			}
		},
	})
}

import type {
	ComponentData,
	ExtractedData,
	ExtractionConfig,
	FigmaFile,
	FigmaNode,
	FrameInfo,
	PageInfo,
	SectionInfo,
} from "./types"

const defaultConfig: Required<ExtractionConfig> = {
	skipInvisible: true,
}

interface TraversalContext {
	pageId: string
	frameId: string
}

function isFrameType(type: string): boolean {
	return type === "FRAME" || type === "COMPONENT" || type === "COMPONENT_SET"
}

export function extractFigmaStructure(
	fileData: FigmaFile,
	config: ExtractionConfig = {},
): ExtractedData {
	const { skipInvisible } = { ...defaultConfig, ...config }

	const shouldSkipNode = (node: FigmaNode): boolean => {
		if (skipInvisible && node.visible === false) {
			return true
		}
		return false
	}

	const filterInvisibleNodes = (node: FigmaNode): FigmaNode => {
		if (!skipInvisible) return node

		const filtered = { ...node }
		if (filtered.children) {
			filtered.children = filtered.children
				.filter((child) => child.visible !== false)
				.map((child) => filterInvisibleNodes(child))
		}
		return filtered
	}

	const componentUsage = new Map<
		string,
		{
			usedInFrames: Set<string>
			instanceCount: number
		}
	>()

	const pages: PageInfo[] = []
	const allSections = new Map<string, SectionInfo>()
	const allFrames = new Map<string, FrameInfo>()
	const componentDefinitions = new Map<string, FigmaNode>()
	const frameComponents = new Map<string, Set<string>>()

	const getComponentName = (id: string): string => {
		return fileData.components[id]?.name || "Unknown Component"
	}

	const getComponentDescription = (id: string): string | undefined => {
		return fileData.components[id]?.description
	}

	const cloneNode = (node: FigmaNode): FigmaNode => {
		return JSON.parse(JSON.stringify(node))
	}

	function collectSections(
		node: FigmaNode,
		parentSectionId?: string,
	): string[] {
		const sectionIds: string[] = []

		if (!node.children) return sectionIds

		for (const child of node.children) {
			if (shouldSkipNode(child)) continue

			if (child.type === "SECTION") {
				const sectionInfo: SectionInfo = {
					id: child.id,
					name: child.name,
					parentSectionId,
					frameIds: [],
				}

				if (child.children) {
					for (const grandChild of child.children) {
						if (!shouldSkipNode(grandChild) && isFrameType(grandChild.type)) {
							sectionInfo.frameIds.push(grandChild.id)
						}
					}
				}

				allSections.set(child.id, sectionInfo)
				sectionIds.push(child.id)

				const nestedSectionIds = collectSections(child, child.id)
				sectionIds.push(...nestedSectionIds)
			}
		}

		return sectionIds
	}

	function traverseFrame(node: FigmaNode, context: TraversalContext) {
		if (shouldSkipNode(node)) return

		if (node.type === "COMPONENT") {
			componentDefinitions.set(node.id, filterInvisibleNodes(cloneNode(node)))
		}

		if (node.type === "COMPONENT_SET") {
			componentDefinitions.set(node.id, filterInvisibleNodes(cloneNode(node)))
			if (node.children) {
				for (const variant of node.children) {
					if (variant.type === "COMPONENT" && !shouldSkipNode(variant)) {
						componentDefinitions.set(
							variant.id,
							filterInvisibleNodes(cloneNode(variant)),
						)
					}
				}
			}
		}

		if (node.type === "INSTANCE" && node.componentId) {
			const compId = node.componentId

			frameComponents.get(context.frameId)?.add(compId)

			const existing = componentUsage.get(compId)
			if (existing) {
				existing.instanceCount++
				existing.usedInFrames.add(context.frameId)
			} else {
				componentUsage.set(compId, {
					instanceCount: 1,
					usedInFrames: new Set([context.frameId]),
				})
			}
		}

		if (node.children) {
			for (const child of node.children) {
				traverseFrame(child, context)
			}
		}
	}

	function collectFrames(
		node: FigmaNode,
		pageId: string,
		sectionId?: string,
	): FrameInfo[] {
		const frames: FrameInfo[] = []

		if (!node.children) return frames

		for (const child of node.children) {
			if (shouldSkipNode(child)) continue

			if (child.type === "SECTION") {
				const sectionFrames = collectFrames(child, pageId, child.id)
				frames.push(...sectionFrames)
			} else if (isFrameType(child.type)) {
				const frameInfo: FrameInfo = {
					id: child.id,
					name: child.name,
					type: child.type,
					pageId,
					sectionId,
					componentsUsed: [],
				}

				frameComponents.set(child.id, new Set())

				traverseFrame(child, {
					pageId,
					frameId: child.id,
				})

				frameInfo.componentsUsed = Array.from(
					frameComponents.get(child.id) || [],
				)

				frames.push(frameInfo)
				allFrames.set(child.id, frameInfo)
			}
		}

		return frames
	}

	if (fileData.document.children) {
		for (const pageNode of fileData.document.children) {
			if (pageNode.type === "CANVAS") {
				const pageId = pageNode.id
				const pageName = pageNode.name

				const sectionIds = collectSections(pageNode)
				const frames = collectFrames(pageNode, pageId)

				pages.push({
					id: pageId,
					name: pageName,
					sectionIds,
					frameIds: frames.map((f) => f.id),
				})
			}
		}
	}

	const components: Record<string, ComponentData> = {}

	for (const [compId, usage] of componentUsage.entries()) {
		components[compId] = {
			name: getComponentName(compId),
			description: getComponentDescription(compId),
			definition: componentDefinitions.get(compId) || null,
			usedInFrames: Array.from(usage.usedInFrames),
			instanceCount: usage.instanceCount,
		}
	}

	return {
		pages,
		sections: Object.fromEntries(allSections),
		frames: Object.fromEntries(allFrames),
		components,
	}
}

export function getComponent(
	data: ExtractedData,
	nameOrId: string,
): ComponentData | null {
	if (data.components[nameOrId]) {
		return data.components[nameOrId]
	}

	const lowerName = nameOrId.toLowerCase()
	for (const comp of Object.values(data.components)) {
		if (comp.name.toLowerCase() === lowerName) {
			return comp
		}
	}

	return null
}

export function listComponents(
	data: ExtractedData,
): { id: string; name: string; instanceCount: number }[] {
	return Object.entries(data.components)
		.map(([id, comp]) => ({
			id,
			name: comp.name,
			instanceCount: comp.instanceCount,
		}))
		.sort((a, b) => b.instanceCount - a.instanceCount)
}

export function listFrames(data: ExtractedData): FrameInfo[] {
	return Object.values(data.frames)
}

export function getFrame(
	data: ExtractedData,
	nameOrId: string,
): FrameInfo | null {
	if (data.frames[nameOrId]) {
		return data.frames[nameOrId]
	}

	const lowerName = nameOrId.toLowerCase()
	for (const frame of Object.values(data.frames)) {
		if (frame.name.toLowerCase() === lowerName) {
			return frame
		}
	}

	return null
}
